PROCEDURE scp_conta_documento_clone(
    v_ID_ENTIDAD_FROM IN NUMBER,
    v_ID_DEPTO_FROM IN VARCHAR2,
    v_ID_ENTIDAD_TO IN NUMBER,
    v_ID_DEPTO_TO IN VARCHAR2,
    v_ERROR OUT NUMBER,
    v_MSGERROR OUT VARCHAR2
)
AS
    L_ERROR NUMBER :=0;
    L_MSGERROR VARCHAR2(100) :='';
    L_ID_DOCUMENTO NUMBER;

	TYPE ContaDocumentoDetalleCurTyp  IS REF CURSOR;
	v_documento_det_cursor    ContaDocumentoDetalleCurTyp;
	d_documento_det_record      CONTA_DOCUMENTO_DETALLE%ROWTYPE;
	v_documento_det_stmt_str      VARCHAR2(200);

    cursor C_DOCUMENTO is
	SELECT * FROM eliseo.CONTA_DOCUMENTO
	WHERE ID_ENTIDAD = v_ID_ENTIDAD_FROM
    AND ID_DEPTO = v_ID_DEPTO_FROM;

BEGIN

    IF L_ERROR = 0 THEN 


        FOR rec_1 in C_DOCUMENTO
        LOOP
                DBMS_OUTPUT.put_line('ID_COMPROBANTE: ' || rec_1.ID_COMPROBANTE);
                DBMS_OUTPUT.put_line('ID_ENTIDAD_TO: ' || v_ID_ENTIDAD_TO);
                DBMS_OUTPUT.put_line('ID_DEPTO_TO: ' || v_ID_DEPTO_TO);

                INSERT INTO ELISEO.CONTA_DOCUMENTO(
                --ID_DOCUMENTO, 
                ID_COMPROBANTE, ID_ENTIDAD, ID_DEPTO, NOMBRE, PUERTO, NUMLINE, NUMCOL, 
                SERIE, CONTADOR, ACTIVO
                --, DOCID
                )
                VALUES(
                rec_1.ID_COMPROBANTE, v_ID_ENTIDAD_TO, v_ID_DEPTO_TO, rec_1.NOMBRE, rec_1.PUERTO, rec_1.NUMLINE, rec_1.NUMCOL,
                rec_1.SERIE, rec_1.CONTADOR, rec_1.ACTIVO
                --, rec_1.DOCID
                ) RETURNING ID_DOCUMENTO INTO L_ID_DOCUMENTO;


                v_documento_det_stmt_str := 'SELECT * FROM ELISEO.CONTA_DOCUMENTO_DETALLE WHERE ID_DOCUMENTO = :1';
                OPEN v_documento_det_cursor FOR v_documento_det_stmt_str USING rec_1.ID_DOCUMENTO;
                    LOOP
                    FETCH v_documento_det_cursor INTO d_documento_det_record;
                    EXIT WHEN v_documento_det_cursor%NOTFOUND;

                        INSERT INTO ELISEO.CONTA_DOCUMENTO_DETALLE(
