SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE        spc_bi_fondo_estimulo_detalle (
    p_id_entidad           int,
    p_id_anho              int,
    p_id_mes_fin           int,
    p_id_depto             varchar2,
    cursor       OUT SYS_REFCURSOR
)
IS
    l_num_documento varchar2(100);

BEGIN
    l_num_documento:= '';


    OPEN cursor FOR 
		SELECT id_mes,id_anho,lote,comentario,id_depto,debe, haber, cos_valor
		FROM VW_CONTA_DIARIO
		WHERE ID_ENTIDAD = 17112
		AND ID_ANHO = p_id_anho

		AND id_mes BETWEEN 1 AND p_id_mes_fin
		AND (
			CASE WHEN ID_TIPOASIENTO='BB' THEN 1 ELSE 
			CASE WHEN ID_CUENTAAASI LIKE '31%' OR ID_CUENTAAASI LIKE '41%' THEN 1 ELSE 0 END
			END) = 1
		AND ID_DEPTO = p_id_depto
        ;
END;

