SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE        spc_user_session_login_app (
    p_id_persona       varchar2,
    p_token       varchar2,
    p_caduca varchar2,
    p_error     OUT number
)
IS
  l_error number:=0;
  l_cant INT;
    BEGIN
 
        
        select count(PER_ID) into l_cant
        from USERS_SESSION_ACADEMICO where token = p_token and status = '1';
        
        IF l_cant > 0 THEN
            update USERS_SESSION_ACADEMICO set LAST_UPDATE = current_timestamp where token = p_token and status = '1';
        ELSE
            insert into USERS_SESSION_ACADEMICO
                (TOKEN,PER_ID,DATE_CREATE,LAST_UPDATE,CADUCA,STATUS) 
            values  (p_token,p_id_persona,current_timestamp,current_timestamp,p_caduca,'1');
        END IF;
    
    p_error:=l_error;
    
END;

