PROCEDURE  sp_audit_tabla (
P_tabla varchar2,
P_id varchar2, 
P_idvalor varchar2, 
p_idpersona integer,
P_ip varchar2,
P_accion varchar2,
P_columna tablastring, 
P_anterior tablastring, 
P_actual tablastring
) AS

s_columna varchar2(150); 
s_anterior varchar2(150); 
s_actual varchar2(150);
s_id varchar2(25);
s_id_log varchar2(25);
--s_iddet varchar2(5);
s_usuario varchar2(60);
s_ip varchar2(15);
BEGIN
    
    s_id:= to_char(sysdate,'YYYYMMDDHH24MISS');
    
    s_ip:= SYS_CONTEXT('USERENV', 'IP_ADDRESS', 15);
    
    select SYS_CONTEXT('USERENV', 'OS_USER') into s_usuario from dual;
    
    if p_idpersona<>0 then
      s_ip:='';
      s_usuario:='';
    end if;

    
    FOR i IN P_columna.FIRST .. P_columna.LAST
    LOOP
      s_columna:=P_columna(i);
       
      s_actual:=P_actual(i);
      
      if P_accion in('I','D') then
          s_anterior:='';
      else
          s_anterior:=P_anterior(i);
      end if;
      s_anterior:=coalesce(s_anterior,' ');
      s_actual:=coalesce(s_actual,' ');
      if P_accion in('I','D') or (s_anterior<>s_actual and P_accion='U') then
      
        --select to_char(cast( dbms_random.value(1,1000) as int)) into s_iddet from dual;
        DBMS_OUTPUT.put_line('Vitmar doc');
        
        DBMS_OUTPUT.put_line(P_idvalor);
        DBMS_OUTPUT.put_line(s_id||to_char(i)||P_idvalor);
        DBMS_OUTPUT.put_line(to_char(i));
        insert into audit_log(
          id_log,
          logi,
          desc_id,
          tabla_id,
          tabla,
          columna,
          accion,
          id_persona,
          ip,
          fechahora,
          anterior,
          actual,
          usuario,
          ip_bd
          )values(
          --s_id||to_char(i)||s_iddet,
          s_id||to_char(i)||P_idvalor,
          s_id,
          P_id,
          P_idvalor,
          P_tabla,
          s_columna,
          P_accion,
          coalesce(p_idpersona,0),
          coalesce(P_ip,' '),
          sysdate,
          s_anterior,
          s_actual,
          s_usuario,
          s_ip
        );
      
      end if;
      
    END LOOP;

   
END;

