CREATE OR REPLACE VIEW vw_conta_documento_salida (id_usuariosalida, id_persona, id_salidadepto, id_entidad, id_depto, id_tipodocumentosalida, nom_docsalida, codigo, id_documento, nom_documento) AS SELECT  CDUS.ID_USUARIOSALIDA, CDUS.ID_PERSONA, CDSD.ID_SALIDADEPTO,CD.ID_ENTIDAD,
       CD.ID_DEPTO, TDS.ID_TIPODOCUMENTOSALIDA, TDS.NOMBRE NOM_DOCSALIDA,TDS.CODIGO,CD.ID_DOCUMENTO,cd.NOMBRE NOM_DOCUMENTO
FROM CONTA_DOCUMENTO_USUARIO_SALIDA CDUS
    INNER JOIN CONTA_DOCUMENTO_SALIDA_DEPTO CDSD ON CDSD.ID_SALIDADEPTO = CDUS.ID_SALIDADEPTO
    INNER JOIN CONTA_DOCUMENTO CD ON CD.ID_DOCUMENTO=CDSD.ID_DOCUMENTO
    INNER JOIN TIPO_DOCUMENTO_SALIDA TDS ON TDS.ID_TIPODOCUMENTOSALIDA = CDSD.ID_TIPODOCUMENTOSALIDA and TDS.ESTADO=1;

