SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON





CREATE OR REPLACE FUNCTION eliseo.detalles_contrato_debito (s_id_contrato text) RETURNS varchar AS $body$
DECLARE

	p_detalles  varchar(512);
	c_detalle_matricula CURSOR FOR
		 SELECT '; '||act.descripcion||' ('||trim(both TO_CHAR(act.importe*CASE WHEN act.dc='D' THEN 1  ELSE -1 END ,'999,990.99'))||')' descripcion
     FROM eliseo.mat_alumno_contrato_det act
     WHERE act.ID_CRITERIO_SEMESTRE in (SELECT  csa.ID_CRITERIO_SEMESTRE from eliseo.mat_criterio_semestre_asiento  csa)
       and act.id_alumno_contrato = s_id_contrato
       and act.dc = 'D'
    order by act.id_alumno_contrato_det;

BEGIN
    p_detalles := '';

    for detalle in  c_detalle_matricula  LOOP
        p_detalles := p_detalles||detalle.descripcion;
    end LOOP;

    p_detalles := substr(p_detalles,2);



  RETURN( p_detalles );

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
