CREATE OR REPLACE FUNCTION eliseo.fc_bdv_proyectado (P_ID_ENTIDAD bigint, P_ID_PERSONA bigint, P_ID_ANHO bigint) RETURNS bigint AS $body$
DECLARE

contar_temp integer:=0;
total_meses decimal(10,2):=0.00;
amount_temp decimal(10,2):=0.00;

BEGIN
	FOR mes IN 1..12
	LOOP
		SELECT count(*) into STRICT contar_temp
		FROM  APS_PLANILLA_DETALLE WHERE ID_ANHO = P_ID_ANHO AND ID_CONCEPTOAPS = 1086 AND ID_MES=mes AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_PERSONA = P_ID_PERSONA;
		IF contar_temp > 0 THEN
			SELECT SUM(COS_VALOR) INTO STRICT amount_temp 
			FROM  APS_PLANILLA_DETALLE WHERE ID_ANHO = P_ID_ANHO AND ID_CONCEPTOAPS = 1086 AND ID_MES=mes AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_PERSONA = P_ID_PERSONA;
		END IF;

		total_meses := total_meses + amount_temp;
	END LOOP;
	RETURN(total_meses);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
