CREATE OR REPLACE FUNCTION eliseo.fc_bon_esp_voluntaria (P_ID_ENTIDAD bigint, P_ID_PERSONA bigint, FECHA_INI timestamp(0), FECHA_FIN timestamp(0)) RETURNS bigint AS $body$
DECLARE

l_amount decimal(10,2):=0.00;
l_contar integer:=0;
l_contar_int integer:=0;
anho bigint;
mes bigint;

BEGIN

	IF EXTRACT(YEAR FROM FECHA_INI) = EXTRACT(YEAR FROM FECHA_FIN) THEN
		--CALCULA ENTRE DOS FECHAS DEL MISMO AÑO
		SELECT COUNT(*) INTO STRICT l_contar FROM APS_PLANILLA_DETALLE
		WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA;
		
		IF l_contar > 0 THEN
			IF l_contar = 1 THEN
					SELECT COS_VALOR INTO STRICT l_amount FROM  APS_PLANILLA_DETALLE
					WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
						AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA;
			ELSE
				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE
				WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA ),0,4) "Substring" into STRICT anho
					;

				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE
				WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
						AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA ),5,2) "Substring" INTO STRICT mes
						;

				SELECT COUNT(*) INTO STRICT l_contar_int FROM  APS_PLANILLA_DETALLE
				WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1300 AND ID_PERSONA = P_ID_PERSONA;
				
				IF l_contar_int > 0 THEN
					SELECT COS_VALOR INTO STRICT l_amount FROM  APS_PLANILLA_DETALLE
					WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA AND ID_ANHO = anho AND ID_MES = mes-1;
				ELSE
					SELECT COS_VALOR INTO STRICT l_amount FROM  APS_PLANILLA_DETALLE
					WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA AND ID_ANHO = anho AND ID_MES = mes;
				END IF;
			END IF;
		END IF;
	ELSE
		--CALCULA ENTRE DOS FECHAS DE DIFERENTES AÑOS
		SELECT COUNT(*) INTO STRICT l_contar FROM APS_PLANILLA_DETALLE
		WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA;
		
		IF l_contar > 0 THEN
			IF l_contar = 1 THEN
					SELECT COS_VALOR INTO STRICT l_amount FROM  APS_PLANILLA_DETALLE
					WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
						AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA;
			ELSE
				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE
				WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA ),0,4) "Substring" into STRICT anho
					;

				SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA_DETALLE
				WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
						AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA ),5,2) "Substring" INTO STRICT mes
						;

				SELECT COUNT(*) INTO STRICT l_contar_int FROM  APS_PLANILLA_DETALLE
				WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
					AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1300 AND ID_PERSONA = P_ID_PERSONA;
				
				IF l_contar_int > 0 THEN
					SELECT COS_VALOR INTO STRICT l_amount FROM  APS_PLANILLA_DETALLE
					WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA AND ID_ANHO = anho AND ID_MES = mes-1;
				ELSE
					SELECT COS_VALOR INTO STRICT l_amount FROM  APS_PLANILLA_DETALLE
					WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1079 AND ID_PERSONA = P_ID_PERSONA AND ID_ANHO = anho AND ID_MES = mes;
				END IF;
			END IF;
		END IF;
	END IF;

	
	RETURN(l_amount);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
