CREATE OR REPLACE FUNCTION eliseo.fc_caja_deposito_venta (P_ID_DEPOSITO integer) RETURNS varchar AS $body$
DECLARE

s_venta varchar(20);
s_resul varchar(1000);
p_conta integer;

DEPOSITO_VENTA CURSOR FOR
SELECT C.SERIE||'-'||C.NUMERO AS NUMERO
FROM CAJA_DEPOSITO A, CAJA_DEPOSITO_DETALLE B, VENTA C
WHERE A.ID_DEPOSITO = B.ID_DEPOSITO
AND B.ID_VENTA = C.ID_VENTA
AND A.ID_DEPOSITO = P_ID_DEPOSITO
AND A.ESTADO = '1'
GROUP BY C.SERIE, C.NUMERO;



BEGIN
    p_conta:=1;
    open DEPOSITO_VENTA;
        fetch DEPOSITO_VENTA into
            s_venta;
        while DEPOSITO_VENTA%found loop
            if p_conta = 1 then
                s_resul := s_venta;
            else
                s_resul := s_resul||', '||s_venta;
            end if;

            fetch DEPOSITO_VENTA into
                s_venta;
            p_conta:=p_conta+1;
        end loop;
	close DEPOSITO_VENTA;	
	
	RETURN(s_resul);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
