CREATE OR REPLACE FUNCTION eliseo.fc_codigo_articulo (P_ID_ARTICULO bigint,P_TIPO text) RETURNS varchar AS $body$
DECLARE

    L_CODIGO varchar(20);

BEGIN 
    SELECT 
            CASE WHEN P_TIPO='1' THEN CODIGO  ELSE SUBSTR(CODIGO,1,8) END  INTO STRICT L_CODIGO
    FROM INVENTARIO_ARTICULO
    WHERE ID_ARTICULO = P_ID_ARTICULO;
    RETURN(L_CODIGO);
   
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
