CREATE OR REPLACE FUNCTION eliseo.fc_codigo_unidad (P_ID_ALMACEN bigint,P_ID_ARTICULO bigint) RETURNS varchar AS $body$
DECLARE

   L_CODIGO   varchar(10);

BEGIN
    SELECT
    coalesce(C.CODIGO_SUNAT,'') INTO STRICT L_CODIGO
    FROM INVENTARIO_ALMACEN_ARTICULO A, INVENTARIO_ARTICULO B, INVENTARIO_UNIDAD_MEDIDA C
    WHERE A.ID_ARTICULO = B.ID_ARTICULO
    AND B.ID_UNIDADMEDIDA = C.ID_UNIDADMEDIDA
    AND A.ID_ALMACEN = P_ID_ALMACEN
    AND A.ID_ARTICULO = P_ID_ARTICULO;
    RETURN(L_CODIGO);
END;
$body$
LANGUAGE PLPGSQL
