CREATE OR REPLACE FUNCTION eliseo.fc_codigo_unsp ( P_ID_ARTICULO bigint) RETURNS varchar AS $body$
DECLARE

  L_CODIGO varchar(8);

BEGIN
  SELECT a.CODIGO
  INTO STRICT L_CODIGO
  FROM eliseo.INVENTARIO_ARTICULO A
  INNER JOIN eliseo.INVENTARIO_ARTICULO B
  ON A.ID_ARTICULO   =B.ID_PARENT
  WHERE B.ID_ARTICULO=P_ID_ARTICULO;
  RETURN(L_CODIGO);
END;
