CREATE OR REPLACE FUNCTION eliseo.fc_cuo_correlativo (P_ID_MOV bigint, p_id_entidad bigint, p_id_anho bigint, p_id_mes bigint) RETURNS varchar AS $body$
DECLARE

L_NOMBRE varchar(100);

BEGIN
	SELECT max('M'||NUM_AASI) INTO STRICT L_NOMBRE FROM VW_CONTA_DIARIO
	WHERE OBSERVACION = trim(both to_char(P_ID_MOV,'9999999999999999999'))
	AND ID_ENTIDAD = p_id_entidad
	AND ID_ANHO = p_id_anho
	AND ID_MES = p_id_mes
	AND ID_CUENTAAASI = 2130101;

        RETURN(L_NOMBRE);

END;
