CREATE OR REPLACE FUNCTION eliseo.fc_depto_by_function (P_ID_COMPRA bigint) RETURNS varchar AS $body$
DECLARE

   p_return		  		   varchar(125);

BEGIN

     p_return := '';

        SELECT COALESCE(max(pdg.ID_DEPTO),'') into STRICT p_return  FROM PEDIDO_COMPRA pc
        INNER JOIN PEDIDO_DIST_GASTO pdg ON pdg.ID_PEDIDO = pc.ID_PEDIDO 
        WHERE pc.ID_COMPRA = P_ID_COMPRA;

	 RETURN( p_return );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
