CREATE OR REPLACE FUNCTION eliseo.fc_documento_cliente (P_ID_CLIENTE bigint) RETURNS varchar AS $body$
DECLARE

  l_datos varchar(250);
 datos CURSOR FOR
                SELECT
                NUM_DOCUMENTO AS NOMBRE
                FROM MOISES.VW_PERSONA_NATURAL
                WHERE ID_PERSONA=P_ID_CLIENTE
                AND ID_TIPODOCUMENTO NOT IN (97,98) --DOCUMENTOS DE AFP - ONP
 
                
UNION
 
                SELECT 
                    ID_RUC AS NOMBRE
                FROM MOISES.VW_PERSONA_JURIDICA 
                WHERE ID_PERSONA=P_ID_CLIENTE;


BEGIN
       open datos;
       fetch datos into l_datos;
       if not datos%found then
         l_datos := '0000';
       end if;
       close datos;
       RETURN( l_datos );

        
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
