CREATE OR REPLACE FUNCTION eliseo.fc_entidades_asignadas (P_ID integer) RETURNS varchar AS $body$
DECLARE

   p_name		  		   varchar(1000);
   p_dato                  varchar(255);
   cont                     integer;
   datos CURSOR FOR
       SELECT
        A.ID_ENTIDAD||' - '||B.NOMBRE AS NOMBRE
        FROM CONTA_ENTIDAD_USUARIO A, CONTA_ENTIDAD B
        WHERE A.ID_ENTIDAD = B.ID_ENTIDAD
        AND A.ID_PERSONA = P_ID;

BEGIN
	 p_name:='';
	 cont:=0;
	 OPEN datos;
                FETCH datos INTO p_dato;

                WHILE datos%FOUND LOOP
                cont:=cont+1;
                if cont = 1 then
                    p_name :=p_dato;
                else
                    p_name :=p_name||', '||p_dato;
                end if;

                FETCH datos INTO p_dato;

                END LOOP;
    CLOSE datos;
    RETURN(p_name);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
