CREATE OR REPLACE FUNCTION eliseo.fc_formato_importe (P_IMPORTE bigint) RETURNS varchar AS $body$
DECLARE

   L_IMPORTE   varchar(50);

BEGIN
   SELECT TO_CHAR(coalesce((
             CASE
                WHEN P_IMPORTE = 0
                THEN
                   '0.00'
                WHEN P_IMPORTE BETWEEN 0.01 AND 0.99
                THEN
                   trim(both TO_CHAR(P_IMPORTE, '90D99'))
                ELSE
                   LTRIM(TO_CHAR(P_IMPORTE, '9999999999D99'))
             END
             ),'fm999999999990.00')) INTO STRICT L_IMPORTE
;
    RETURN(P_IMPORTE);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
