CREATE OR REPLACE FUNCTION eliseo.fc_gerarquia_level (P_ID_MODULO text ) RETURNS varchar AS $body$
DECLARE
 V_ANSWER varchar(200);


BEGIN

WITH RECURSIVE cte AS (
select 1 as level  from lamb_modulo WHERE id_modulo = P_ID_MODULO
  UNION ALL
select (c.level+1)  from lamb_modulo JOIN cte c ON (c.id_padre = id_modulo)

) SELECT * INTO STRICT V_ANSWER FROM cte WHERE coalesce(id_padre::text, '') = '';

  RETURN V_ANSWER;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
