CREATE OR REPLACE FUNCTION eliseo.fc_get_gran_parent_name (P_ID_MODULO text,P_NIVEL text ) RETURNS varchar AS $body$
DECLARE
 V_ANSWER varchar(200);


BEGIN

WITH RECURSIVE cte AS (
select NOMBRE  from lamb_modulo WHERE id_modulo = P_ID_MODULO
  UNION ALL
select NOMBRE  from lamb_modulo JOIN cte c ON (c.id_padre = id_modulo)

) SELECT * INTO STRICT V_ANSWER FROM cte WHERE NIVEL =  P_NIVEL;

  RETURN V_ANSWER;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
