CREATE OR REPLACE FUNCTION eliseo.fc_llave_proceso_previous (P_ID_REGISTRO bigint) RETURNS varchar AS $body$
DECLARE

    L_LLAVE varchar(20);

BEGIN
    SELECT
            FC_GET_LLAVE_COMPONENTE(A.ID_REGISTRO,A.ID_PASO,1) INTO STRICT L_LLAVE
    FROM PROCESS_PASO_RUN A, PROCESS_PASO B
    WHERE A.ID_PASO = B.ID_PASO
    AND A.ID_REGISTRO = P_ID_REGISTRO
    AND B.ID_TIPOPASO = 2
    AND A.ID_DETALLE = (SELECT MAX(ID_DETALLE) FROM PROCESS_PASO_RUN WHERE ID_REGISTRO = P_ID_REGISTRO AND ESTADO = '1');

    RETURN L_LLAVE;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
