     AND b.ID_ENTIDAD = P_ID_ENTIDAD;


BEGIN
	 open datos;
	 fetch datos into p_name;
	 if not datos%found then
	   p_name := '';
	 end if;
	 close datos;
	 RETURN( p_name );
END;
