CREATE OR REPLACE FUNCTION eliseo.fc_nameentity (COD_ENTIDAD bigint) RETURNS varchar AS $body$
DECLARE

   p_name		  		   varchar(100);
   datos CURSOR FOR
       SELECT
	       nombre
	   from conta_entidad
	   where ID_ENTIDAD = COD_ENTIDAD;

BEGIN
	 open datos;
	 fetch datos into p_name;
	 if not datos%found then
	   p_name := '';
	 end if;
	 close datos;
	 RETURN( p_name );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
