CREATE OR REPLACE FUNCTION eliseo.fc_namesistemapension (ID_SP bigint) RETURNS varchar AS $body$
DECLARE

   p_name		  		   varchar(25);
   datos CURSOR FOR
       SELECT
	       nombre
	   from APS_SISTEMA_PENSION
	   where ID_SISTEMAPENSION = ID_SP;

BEGIN
	 open datos;
	 fetch datos into p_name;
	 if not datos%found then
	   p_name := '';
	 end if;
close datos;
	 RETURN( p_name );
END;
$body$
