CREATE OR REPLACE FUNCTION eliseo.fc_nombre_persona_ruc (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

  l_datos varchar(250);
 datos CURSOR FOR
                SELECT
                NOMBRE AS NOM_PERSONA
                FROM MOISES.VW_PERSONA_NATURAL_LEGAL
                WHERE ID_PERSONA=P_ID_PERSONA;


BEGIN
       open datos;
       fetch datos into l_datos;
       if not datos%found then
         l_datos := ' ';
       end if;
       close datos;
       RETURN( l_datos );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
