CREATE OR REPLACE FUNCTION eliseo.fc_numero_compra ( p_idorigen bigint) RETURNS varchar AS $body$
DECLARE

  p_nombre varchar(20);
  dato CURSOR FOR
    SELECT numero FROM eliseo.COMPRA
    where ID_COMPRA=p_idorigen;

BEGIN
  OPEN dato;
  FETCH dato INTO p_nombre;
  IF NOT dato%found THEN
    p_nombre := ' ';
  END IF;
CLOSE dato;
RETURN(p_nombre);
END;
$body$
LANGUAGE PLPGSQL
