CREATE OR REPLACE FUNCTION eliseo.fc_numtxt_centenas (p_numero bigint) RETURNS varchar AS $body$
DECLARE

   r_numero  varchar(60);
   numeros CURSOR FOR
       SELECT
	       numtxt
	   from NUMEROS
	   where numero = mod(p_numero,1000)-mod(p_numero,100);

BEGIN

	 if mod(p_numero,1000) = 100 then
	 	 RETURN( 'cien ' );
	 end if;

	 open numeros;
	 fetch numeros into r_numero;

	 if NOT FOUND then
	 	r_numero := '';
	 end if;

	 close numeros;
	 RETURN( r_numero||' ' );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
