CREATE OR REPLACE FUNCTION eliseo.fc_numtxt_decenas (p_numero bigint) RETURNS varchar AS $body$
DECLARE

   r_numero  varchar(60);
   t_numero  smallint;
   numeros CURSOR FOR
       SELECT
	       numtxt
	   from NUMEROS
	   where numero = t_numero;

BEGIN
	 if mod(p_numero,100)>=10 and mod(p_numero,100)<=19 then
	 	 t_numero := mod(p_numero,100);
	 	 open numeros;
	 	 fetch numeros into r_numero;
		 if NOT FOUND then
		  	r_numero := '';
		 end if;
		 close numeros;
	 	RETURN( r_numero );
	 end if;


--	 if mod(p_numero,10)=0 then
	 	if mod(p_numero,100) = 20 then
		   RETURN( 'Veinte' );
		end if;
--	 else
	 	 t_numero := mod(p_numero-mod(p_numero,10),100);
	 	 open numeros;
	 	 fetch numeros into r_numero;
		 if NOT FOUND then
		  	r_numero := '';
		 end if;
		 close numeros;

		if mod(p_numero,10)>0 and mod(p_numero,100) > 29 then
		   r_numero := r_numero || ' y ';
		end if;

--	end if;
	 RETURN( r_numero );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
