CREATE OR REPLACE FUNCTION eliseo.fc_numtxt_unidades (p_numero bigint) RETURNS varchar AS $body$
DECLARE

   r_numero  varchar(60);
   numeros CURSOR FOR
       SELECT
	       numtxt
	   from NUMEROS
	   where numero = mod(p_numero,10);

BEGIN
	 r_numero := '';
	 if mod(p_numero,100)<10 or mod(p_numero,100)>19 then
	 	 open numeros;
		 fetch numeros into r_numero;
		 if NOT FOUND then
		 	r_numero := '';
		 end if;
		 close numeros;
	 end if;
	 RETURN( r_numero );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
