CREATE OR REPLACE FUNCTION eliseo.fc_obtener_celular (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

l_dato varchar(100):='';
l_contar integer:=0;
l_id bigint;


BEGIN

   select COUNT(*) INTO STRICT l_contar from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO in (3,5);-- and GTH=1;
   IF l_contar>0 THEN
     IF l_contar=1 THEN
      select NUM_TELEFONO INTO STRICT l_dato from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO in (3,5);-- and GTH=1;
     ELSE
      select max(id_TELEFONO) into STRICT l_id from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO in (3,5);-- and GTH=1;
      select NUM_TELEFONO INTO STRICT l_dato from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and id_TELEFONO=l_id and ID_TIPOTELEFONO in (3,5);-- and GTH=1;
     END IF;
   END IF;

	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
