CREATE OR REPLACE FUNCTION eliseo.fc_obt_almacen_area (P_AREA bigint) RETURNS bigint AS $body$
DECLARE

CONTAR integer:=0;
ALMACEN bigint:= NULL;


BEGIN
   SELECT COUNT(*) INTO STRICT CONTAR
			FROM ORG_SEDE_AREA OSA
			INNER JOIN INVENTARIO_ALMACEN IA ON OSA.ID_SEDEAREA = IA.ID_SEDEAREA
			INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
			WHERE OSA.ID_AREA = P_AREA AND OSA.ESTADO='1' AND IA.ESTADO='1' AND ID_RUBRO=1;

   IF CONTAR > 0 THEN
		SELECT DISTINCT IA.ID_ALMACEN INTO STRICT ALMACEN
			FROM ORG_SEDE_AREA OSA
			INNER JOIN INVENTARIO_ALMACEN IA ON OSA.ID_SEDEAREA = IA.ID_SEDEAREA
			INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
			WHERE OSA.ID_AREA = P_AREA AND OSA.ESTADO='1' AND IA.ESTADO='1' AND ID_RUBRO=1;
   END IF;
	RETURN(ALMACEN);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
