CREATE OR REPLACE FUNCTION eliseo.fc_obt_almacen_name (P_ID_ALMACEN bigint) RETURNS varchar AS $body$
DECLARE

	p_name		  		   varchar(150);
   datos CURSOR FOR
			SELECT NOMBRE
			FROM INVENTARIO_ALMACEN
			WHERE ID_ALMACEN = P_ID_ALMACEN;


BEGIN
	 open datos;
	 fetch datos into p_name;
	 if not datos%found then
	   p_name := '';
	 end if;
	 close datos;
	 RETURN( p_name );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
