CREATE OR REPLACE FUNCTION eliseo.fc_pedido_asiento_match (in_id_pedido bigint) RETURNS varchar AS $body$
DECLARE

    v_result varchar(1000);
    v_sumad decimal(10,2);
    v_sumac decimal(10,2);

BEGIN
    SELECT (SELECT coalesce(SUM(PORCENTAJE),0)
        FROM PEDIDO_ASIENTO
        WHERE ID_PEDIDO = in_id_pedido AND DC = 'D')
      ,(SELECT coalesce(SUM(PORCENTAJE),0)
        FROM PEDIDO_ASIENTO
        WHERE ID_PEDIDO = in_id_pedido AND DC = 'C')
      INTO STRICT
      v_sumad
      ,v_sumac
;
    -- 
    IF v_sumad != v_sumac THEN
        v_result := 'NO';
    ELSIF v_sumad != 100 THEN
        IF v_sumad/2 = 100 AND v_sumac/2 = 100 THEN
            v_result := 'SI';
        ELSE
            v_result := 'NO';
        END IF;
    ELSE
        v_result := 'SI';
    END IF;
    RETURN(v_result);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
