CREATE OR REPLACE FUNCTION eliseo.fc_proveedor_rn ( p_idorigen bigint) RETURNS varchar AS $body$
DECLARE

  p_nombre varchar(200);
  dato CURSOR FOR
    SELECT b.empresa
    FROM eliseo.COMPRA a
    JOIN(SELECT b.ID_RUC||' '||a.NOMBRE empresa,
        b.ID_PERSONA
      FROM eliseo.PERSONA a
      JOIN eliseo.PERSONA_JURIDICA b
      ON a.ID_PERSONA      =b.ID_PERSONA
      ) b ON a.ID_PROVEEDOR=b.ID_PERSONA
  WHERE a.id_compra        = p_idorigen;

BEGIN
  OPEN dato;
  FETCH dato INTO p_nombre;
  IF NOT dato%found THEN
    p_nombre := ' ';
  END IF;
CLOSE dato;
RETURN(p_nombre);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
