CREATE OR REPLACE FUNCTION eliseo.fc_provision_grati (P_ID_ENTIDAD bigint, P_ID_PERSONA bigint, P_ANHO bigint, P_MES bigint) RETURNS bigint AS $body$
DECLARE

l_valor decimal(10,2):=0.00;
l_contar integer:=0;

BEGIN
		SELECT COUNT(*) INTO STRICT l_contar FROM APS_PLANILLA_DETALLE
		WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 9100 AND 
			ID_PERSONA = P_ID_PERSONA AND ID_ANHO = P_ANHO AND ID_MES=P_MES;

	IF l_contar >0 THEN
		IF l_contar = 1 THEN
				SELECT COS_VALOR INTO STRICT l_valor FROM APS_PLANILLA_DETALLE
				WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 9100 AND 
					ID_PERSONA = P_ID_PERSONA AND ID_ANHO = P_ANHO AND ID_MES=P_MES;
		ELSE
				SELECT AVG(COS_VALOR) INTO STRICT l_valor FROM APS_PLANILLA_DETALLE
				WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 9100 AND 
					ID_PERSONA = P_ID_PERSONA AND ID_ANHO = P_ANHO AND ID_MES=P_MES;
		END IF;
	END IF;
	RETURN(l_valor);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
