CREATE OR REPLACE FUNCTION eliseo.fc_psto_calc_modforlab (P_VALOR bigint) RETURNS bigint AS $body$
DECLARE

   l_incremento decimal(10,2):=1;
  l_valor decimal(10,2):=0;

BEGIN
   l_valor:=round((P_VALOR)::numeric,0);
   WHILE l_incremento<10 loop

      EXIT WHEN SUBSTR(round((l_valor)::numeric,0)::text,-1) in ('0','5');
      
      l_valor:=l_valor + 1;
      l_incremento:=l_incremento + 1;

    END LOOP;

   RETURN l_valor;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
