CREATE OR REPLACE FUNCTION eliseo.fc_psto_dist_mesnsual (P_ID_ACTIVIDAD bigint) RETURNS varchar AS $body$
DECLARE

l_dato varchar(3000):='';
l_conta integer;




BEGIN
    SELECT COUNT(*) INTO STRICT l_conta FROM PSTO_ACTIVIDAD_DIST
    WHERE ID_ACTIVIDAD=P_ID_ACTIVIDAD;

    IF l_conta>0 THEN
        SELECT X.MESES INTO STRICT l_dato FROM (                
        SELECT string_agg(CASE WHEN B.TIPO_DIST='PO' THEN M.SIGLAS||' '||A.PORCENTAJE||'%' ELSE M.SIGLAS END, ', ' ORDER BY A.ID_MES) OVER (PARTITION BY A.ID_ACTIVIDAD) MESES,A.ID_ACTIVIDAD 
        FROM PSTO_ACTIVIDAD_DIST A, PSTO_ACTIVIDAD B, CONTA_MES M 
        WHERE A.ID_ACTIVIDAD=B.ID_ACTIVIDAD
        AND A.ID_MES=M.ID_MES
        ORDER BY A.ID_ACTIVIDAD
      )X  WHERE X.ID_ACTIVIDAD=P_ID_ACTIVIDAD
      GROUP BY X.MESES;
    END IF;	
	
	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
