CREATE OR REPLACE FUNCTION eliseo.fc_resumen_especie (P_ID_ENTIDAD bigint, P_ID_PERSONA bigint, FECHA_INI timestamp(0), FECHA_FIN timestamp(0)) RETURNS bigint AS $body$
DECLARE

l_re_especie decimal(10,2):=0.00;
l_contar integer:=0; -- Variable para contar
anho bigint; -- El año del reporte
mes bigint; -- Mes de reporte
