	anho:=EXTRACT(YEAR FROM FECHA_FIN);
	mes:=EXTRACT(MONTH FROM FECHA_FIN);
	SELECT COUNT(1) INTO STRICT l_contar FROM APS_PLANILLA_DETALLE
	WHERE ID_MES= mes AND ID_ANHO = anho
			AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1222 AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar >0 THEN
			SELECT SUM(COS_VALOR) INTO STRICT l_re_especie FROM APS_PLANILLA_DETALLE
						WHERE ID_MES= mes AND ID_ANHO = anho
							AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1222 AND ID_PERSONA = P_ID_PERSONA;
		END IF;
