CREATE OR REPLACE FUNCTION eliseo.fc_tipocambio (P_ID_MONEDA bigint,P_FECHA timestamp(0),P_TIPO text) RETURNS bigint AS $body$
DECLARE

  l_tc decimal(10,3);
  datos CURSOR FOR
      SELECT coalesce(case when P_TIPO='C' then cos_compra
                    when P_TIPO='V' then cos_venta 
               else 
                cos_denominacional 
               end,0) as tc
        from TIPO_CAMBIO 
        where id_moneda= P_ID_MONEDA 
        and to_char(fecha,'YYYYMMDD')=to_char(P_FECHA,'YYYYMMDD');

BEGIN

      open datos;
       fetch datos into l_tc;
       if not datos%found then
         l_tc := 0;
       end if;
       close datos;
       RETURN( l_tc );

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
