CREATE OR REPLACE FUNCTION eliseo.fc_unidad_medida (P_ID_ARTICULO integer) RETURNS varchar AS $body$
DECLARE

P_NOMBRE varchar(50);

BEGIN
        SELECT B.NOMBRE INTO STRICT P_NOMBRE
        FROM INVENTARIO_ARTICULO A, INVENTARIO_UNIDAD_MEDIDA B
        WHERE A.ID_UNIDADMEDIDA = B.ID_UNIDADMEDIDA
        AND A.ID_ARTICULO = P_ID_ARTICULO;

        RETURN(P_NOMBRE);
END;
