CREATE OR REPLACE FUNCTION eliseo.fc_vale (P_ID_VALE bigint) RETURNS varchar AS $body$
DECLARE

    L_DATA_VALE varchar(255);

BEGIN
        SELECT
            NUMERO||' - S/. '||IMPORTE||' - '||FC_NOMBRE_PERSONA(ID_EMPLEADO)||' - '||TO_CHAR(FECHA,'DD/MM/YYYY') INTO STRICT L_DATA_VALE
        FROM CAJA_VALE WHERE ID_VALE = P_ID_VALE;

        RETURN(L_DATA_VALE);
END;
