CREATE OR REPLACE FUNCTION eliseo.fc_vehiculo_precio (P_ID_VEHICULO bigint) RETURNS bigint AS $body$
DECLARE

    L_PRECIO decimal(10,2);

BEGIN 
    SELECT
           coalesce(KM_PRECIO,0)  INTO STRICT L_PRECIO
    FROM PEDIDO_VEHICULO
    WHERE ID_VEHICULO = P_ID_VEHICULO;
    RETURN(L_PRECIO);
END;
$body$
