CREATE OR REPLACE FUNCTION eliseo.fc_viaticos_ld (P_ID_ENTIDAD bigint, P_ID_PERSONA bigint, FECHA_INI timestamp(0), FECHA_FIN timestamp(0)) RETURNS bigint AS $body$
DECLARE

l_monto decimal(10,2):=0.00;
l_contar integer:=0;
l_fec_pago timestamp(0);

BEGIN
	
	IF EXTRACT(YEAR FROM FECHA_INI) = EXTRACT(YEAR FROM FECHA_FIN) THEN
		--CALCULA ENTRE DOS FECHAS DEL MISMO AÑO
		SELECT COUNT(*) INTO STRICT l_contar FROM APS_PLANILLA_DETALLE
		WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
			AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1145 AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar >= 3 THEN
			SELECT AVG(COS_VALOR) INTO STRICT l_monto FROM  APS_PLANILLA_DETALLE
			WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1145 AND ID_PERSONA = P_ID_PERSONA;
		END IF;
	ELSE
		--CALCULA ENTRE DOS FECHAS DE DIFERENTES AÑOS
		SELECT COUNT(*) INTO STRICT l_contar FROM APS_PLANILLA_DETALLE
		WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
			AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1145 AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar >= 3 THEN
			SELECT AVG(COS_VALOR) INTO STRICT l_monto FROM  APS_PLANILLA_DETALLE
			WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1145 AND ID_PERSONA = P_ID_PERSONA;
		END IF;
	END IF;

RETURN(l_monto);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
