SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_accounting,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_accounting_fc_departamento_asiento ( p_id_entidad bigint, p_id_tipoorigen bigint, p_id_origen bigint, p_id_voucher bigint, p_dc text ) RETURNS varchar AS $body$
DECLARE

        l_depto varchar(30);

BEGIN
        IF p_dc = 'D' THEN
            SELECT
                MAX(depto)
            INTO STRICT l_depto
            FROM
                conta_asiento
            WHERE
                    id_tipoorigen = p_id_tipoorigen
                AND id_origen = p_id_origen
                AND voucher = p_id_voucher
                AND importe > 0;

        ELSE
            SELECT
                depto
            INTO STRICT l_depto
            FROM
                conta_asiento
            WHERE
                    id_tipoorigen = p_id_tipoorigen
                AND id_origen = p_id_origen
                AND voucher = p_id_voucher
                AND importe < 0;

        END IF;

        RETURN( l_depto );
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
