CREATE OR REPLACE FUNCTION eliseo.pkg_accounting_fc_moneda ( p_id_moneda bigint ) RETURNS varchar AS $body$
DECLARE

        p_nombre varchar(255);

BEGIN
        SELECT
            coalesce(nombre, 'NO EXISTE CUENTA EMPRESARIAL')
        INTO STRICT p_nombre
        FROM
            conta_moneda
        WHERE
            id_moneda = p_id_moneda;

        RETURN( p_nombre );
    END;
$body$
