CREATE OR REPLACE PROCEDURE eliseo.pkg_accounting_sp_add_conta_asiento ( p_id_asiento bigint, p_id_tipoorigen bigint, p_id_origen bigint, p_fondo text, p_depto text, p_cuenta text, p_cuenta_cte text, p_restriccion text, p_importe bigint, p_descripcion text, p_voucher bigint, p_importe_me bigint, p_tipo text ) AS $body$
BEGIN
        IF p_tipo = '1' THEN
            INSERT INTO conta_asiento(
                id_tipoorigen,
                id_origen,
                fondo,
                depto,
                cuenta,
                cuenta_cte,
                restriccion,
                importe,
                descripcion,
                voucher,
                importe_me
            ) VALUES (
                p_id_tipoorigen,
                p_id_origen,
                p_fondo,
                p_depto,
                p_cuenta,
                p_cuenta_cte,
                p_restriccion,
                p_importe,
                p_descripcion,
                p_voucher,
                p_importe_me
            );

        ELSE
            UPDATE conta_asiento
            SET
                fondo = p_fondo,
                depto = p_depto,
                cuenta = p_cuenta,
                cuenta_cte = p_cuenta_cte,
                restriccion = p_restriccion,
                importe = p_importe,
                descripcion = p_descripcion,
                importe_me = p_importe_me
            WHERE
                id_asiento = p_id_asiento;

        END IF;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
