SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_accounting,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_accounting_sp_insert_voucher ( p_id_entidad integer, p_id_depto text, p_id_anho integer, p_id_mes integer, p_fecha timestamp(0), p_id_tipoasiento text, p_id_tipovoucher bigint, p_activo text, p_id_persona bigint, p_id_voucher OUT bigint ) AS $body$
DECLARE

        l_vou_id     bigint;
        l_cont       bigint;
        l_nrovoucher bigint;
        l_automatico varchar(4);

BEGIN
        l_cont := 0;
        RAISE NOTICE 'P_ID_TIPOASIENTO = %', p_id_tipoasiento;
        RAISE NOTICE 'P_ID_TIPOVOUCHER = %', p_id_tipovoucher;
        SELECT
            automatico
        INTO STRICT l_automatico
        FROM
            conta_voucher_config
        WHERE
                id_entidad = p_id_entidad
            AND id_depto = p_id_depto
            AND id_tipoasiento = p_id_tipoasiento
            AND id_anho = p_id_anho
            AND id_tipovoucher = p_id_tipovoucher;

        IF l_automatico = 'S' THEN  -- SI EL VOUCHER ES AUTOMATICO SE CREAN TODOS LOS QUE SEAN NECESARIOS
            SELECT
                coalesce(MAX(numero), 0) + 1
            INTO STRICT l_nrovoucher
            FROM
                conta_voucher
            WHERE
                    id_entidad = p_id_entidad
                AND id_depto = p_id_depto
                AND id_anho = p_id_anho
                AND id_mes = p_id_mes
                AND id_tipoasiento = p_id_tipoasiento
                AND id_tipovoucher = p_id_tipovoucher;

            SELECT
                coalesce(MAX(id_voucher), 0) + 1
            INTO STRICT l_vou_id
            FROM
                conta_voucher;

            p_id_voucher := l_vou_id;
            INSERT INTO conta_voucher(
                id_voucher,
                id_entidad,
                id_depto,
                id_anho,
                id_mes,
                id_tipoasiento,
                id_tipovoucher,
                numero,
                fecha,
                activo,
                id_persona
            ) VALUES (
                l_vou_id,
                p_id_entidad,
                p_id_depto,
                p_id_anho,
                p_id_mes,
                p_id_tipoasiento,
                p_id_tipovoucher,
                l_nrovoucher,
                p_fecha,
                p_activo,
                p_id_persona
            );

        END IF;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
