CREATE OR REPLACE FUNCTION eliseo.pkg_caja_fc_cta_cte_rendicion (P_ID_PAGO bigint) RETURNS varchar AS $body$
DECLARE

        L_CTACTE varchar(255);

BEGIN
            SELECT L_CTACTE INTO STRICT L_CTACTE FROM (
            SELECT coalesce(ID_CTACTE,'') AS L_CTACTE FROM CAJA_PAGO_ASIENTO WHERE ID_PAGO = P_ID_PAGO
            
UNION ALL

            SELECT coalesce(MAX(C.ID_CTACTE),'') AS L_CTACTE
            FROM CAJA_PAGO A JOIN CAJA_VALE B ON A.ID_VALE = B.ID_VALE JOIN CAJA_VALE_ASIENTO C ON B.ID_VALE = C.ID_VALE
            WHERE A.ID_PAGO = P_ID_PAGO
            AND C.IMPORTE > 0
            ) alias3 LIMIT 1;

            RETURN(L_CTACTE);
    END;
$body$
