CREATE OR REPLACE FUNCTION eliseo.pkg_caja_fc_name_cta_bancaria (P_ID_CTABANCARIA bigint) RETURNS varchar AS $body$
DECLARE

        L_CUENTA_CORRIENTE varchar(255);

BEGIN
            SELECT coalesce(A.SIGLA||' - '||B.NOMBRE,'') INTO STRICT L_CUENTA_CORRIENTE
            FROM CAJA_ENTIDAD_FINANCIERA A JOIN CAJA_CUENTA_BANCARIA B ON A.ID_BANCO = B.ID_BANCO
            WHERE B.ID_CTABANCARIA = P_ID_CTABANCARIA;

            RETURN(L_CUENTA_CORRIENTE);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
