SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_caja,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_caja_sp_eliminar_detraccion (P_ID_DETRACCION bigint) AS $body$
DECLARE

        L_ID_VOUCHER integer;
        L_ID_TIPOORIGEN bigint;
        L_ID_ORIGEN bigint;

        c_det_compra CURSOR FOR	
        SELECT ID_TIPOORIGEN, ID_DETDETALLE
        FROM CAJA_DETRACCION_COMPRA 
        WHERE ID_DETRACCION=P_ID_DETRACCION;

        
BEGIN
        
        SELECT ID_VOUCHER INTO STRICT L_ID_VOUCHER 
        FROM CAJA_DETRACCION
        WHERE ID_DETRACCION = P_ID_DETRACCION;

        OPEN c_det_compra;
        FETCH c_det_compra INTO L_ID_TIPOORIGEN, L_ID_ORIGEN;

        WHILE c_det_compra%FOUND LOOP

            DELETE FROM CONTA_ASIENTO WHERE ID_TIPOORIGEN = L_ID_TIPOORIGEN AND ID_ORIGEN=P_ID_DETRACCION AND VOUCHER = L_ID_VOUCHER;

            FETCH c_det_compra INTO L_ID_TIPOORIGEN, L_ID_ORIGEN;
        END LOOP;
        CLOSE c_det_compra;
        
        DELETE FROM CAJA_DETRACCION_COMPRA where ID_DETRACCION = P_ID_DETRACCION;
        DELETE FROM CAJA_DETRACCION where ID_DETRACCION = P_ID_DETRACCION;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
